(74, 1, '2021-06-01 07:18:17', '2021-06-01 07:18:17', '<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":3} -->\n<h3>Lowest Course Fees</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>We are offering the lowest possible course fees for all of our courses in entire Kolkata.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":3} -->\n<h3>Convenient Location</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>We are located beside Rabindra Sarobar Metro Station, near Tollygunge, Kolkata.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":3} -->\n<h3>Flexible Batch Timings</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Our batch timing are completely flexible for both school / College Students and Working Professionals.&nbsp;</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":3} -->\n<h3>Internships &amp; Placements</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>We offer Live Project Training / Internship / Placement Assistance after successful course completion.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:block {\"ref\":970} /-->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Our Core Features</h2>\n<!-- /wp:heading -->\n\n<!-- wp:media-text {\"mediaId\":881,\"mediaLink\":\"http://7star/about-us/sharing-ideas-with-groupmate_1098-17227/\",\"mediaType\":\"image\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://7star/wp-content/uploads/2021/06/sharing-ideas-with-groupmate_1098-17227.jpg\" alt=\"\" class=\"wp-image-881 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":4} -->\n<h4>Classrom &amp; Lab</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Our Classrooms and Labs are equipped with latest equipment and facilities based on the latest technology pertaining to different Design &amp; Technology Sectors.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":882,\"mediaLink\":\"http://7star/about-us/education-concept-student-studying-brainstorming-campus-concept-close-up-students-discussing-their-subject-books-textbooks-selective-focus_1418-627/\",\"mediaType\":\"image\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://7star/wp-content/uploads/2021/06/education-concept-student-studying-brainstorming-campus-concept-close-up-students-discussing-their-subject-books-textbooks-selective-focus_1418-627.jpg\" alt=\"\" class=\"wp-image-882 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":4} -->\n<h4>Faculty</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Our Faculties are Industry Professionals with Decades of Hands on experience in their respective fields. They train our students with individual care and focus on their overall growth.&nbsp;</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaId\":883,\"mediaLink\":\"http://7star/about-us/pretty-freelance-copywriter-texting-message-phone-while-her-colleagues-typing-keyboards-indoor-portrait-young-programmers-different-ethnicities-working-office_197531-3697/\",\"mediaType\":\"image\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://7star/wp-content/uploads/2021/06/pretty-freelance-copywriter-texting-message-phone-while-her-colleagues-typing-keyboards-indoor-portrait-young-programmers-different-ethnicities-working-office_197531-3697.jpg\" alt=\"\" class=\"wp-image-883 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":4} -->\n<h4>Live Project Training</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>After successful completion of their courses, students go through Live Project Training sharpening their skills to become Industry Ready and face the Professional world.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":884,\"mediaLink\":\"http://7star/about-us/7-star-iso-certified/\",\"mediaType\":\"image\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://7star/wp-content/uploads/2021/06/7-STAR-ISO-CERTIFIED.png\" alt=\"\" class=\"wp-image-884 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":4} -->\n<h4>ISO Certified</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>7 Star Academy is an ISO 9001:2015 Certified Institute. We consistently provide quality training to our students and meet the guidelines of training and regulatory requirements .</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->', 'Facilities', '', 'publish', 'closed', 'closed', '', 'facilities', '', '', '2021-06-17 07:23:46', '2021-06-17 07:23:46', '', 7, 'http://7star/?page_id=74', 1, 'page', '', 0),
